import 'package:flutter/material.dart';
import 'package:nomadworld/ui/screens/RouteScreen/widgets/Carousel.dart';

class RouteScreen extends StatelessWidget {
  final List<String> images = [
    'assets/paris.jpg',
    'assets/espana.jpg',
    'assets/japan.jpg',
    'assets/croacia.jpg',
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: CustomScrollView(
            slivers: <Widget>[
              SliverAppBar(
                expandedHeight: MediaQuery.of(context).size.height * 0.3,
                floating: false,
                pinned: true,
                backgroundColor: Color.fromARGB(255, 20, 134, 94),
                flexibleSpace: FlexibleSpaceBar(
                  title: Text('Título de la Ruta'),
                  background: checkImageNumber(),
                ),
              ),
              SliverToBoxAdapter(
                child: Padding(
                  padding: EdgeInsets.all(MediaQuery.of(context).size.height * 0.01),
                  child: Container(
                    color: Colors.green, // Color verde
                    height: 140, // Ajusta la altura del Container según tus necesidades
                    child: SingleChildScrollView(
                      child: Text(""),
                    ),
                  ),
                )
              ),
              SliverList(
                delegate: SliverChildBuilderDelegate(
                      (BuildContext context, int index) {
                    return ListTile(
                      title: Text('Elemento $index'),
                    );
                  },
                  childCount: 20,
                ),
              ),
            ],
      ),
      );
  }

  checkImageNumber(){
    if (images.length > 1){
      return Carousel(images: images);
    }
    else {
      return Container(
        decoration: BoxDecoration(
          image: DecorationImage(
            image: AssetImage(images[0]),
            fit: BoxFit.cover,
          ),
        ),
      );
    }
  }
}

